INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (3, 2, '房地产', '<p>&nbsp;&nbsp;&nbsp;&nbsp;对于房地产的概念，应该从两个方面来理解：房地产既是一种客观存在的物质形态，同时也是一项<a target="_blank" href="http://baike.baidu.com/view/257668.htm" style="text-decoration: none; color: rgb(19, 110, 194);">法律权利</a>。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;作为一种客观存在的物质形态，房地产是指房产和地产的总称，包括土地和土地上永久建筑物及其所衍生的权利。房产是指建筑在土地上的各种房屋，包括住宅、厂房、仓库和商业、服务、文化、教育、卫生、体育以及办公用房等。地产是指土地及其上下一定的空间，包括地下的各种基础设施、地面道路等。<a target="_blank" href="http://baike.baidu.com/view/9346.htm" style="text-decoration: none; color: rgb(19, 110, 194);">房地产</a>由于其自己的特点即位置的固定性和不可移动性，在<a target="_blank" href="http://baike.baidu.com/view/31551.htm" style="text-decoration: none; color: rgb(19, 110, 194);">经济学</a>上又被称为<a target="_blank" href="http://baike.baidu.com/view/10970.htm" style="text-decoration: none; color: rgb(19, 110, 194);">不动产</a>。可以有三种存在形态：即<a target="_blank" href="http://baike.baidu.com/view/36177.htm" style="text-decoration: none; color: rgb(19, 110, 194);">土地</a>、<a target="_blank" href="http://baike.baidu.com/view/20960.htm" style="text-decoration: none; color: rgb(19, 110, 194);">建筑</a>物、房地合一。在<a target="_blank" href="http://baike.baidu.com/view/303408.htm" style="text-decoration: none; color: rgb(19, 110, 194);">房地产拍卖</a>中，其拍卖标的也可以有三种存在形态，即土地（<a target="_blank" href="http://baike.baidu.com/view/19573.htm" style="text-decoration: none; color: rgb(19, 110, 194);">土地使用权</a>）、建筑物和房地合一状态下的物质实体及其权益。随着个人财产所有权的发展，房地产已经成为商业交易的主要组成部分。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;法律意义上的房地产本质是一种财产权利，这种财产权利是指寓含于房地产实体中的各种经济利益以及由此而形成的各种权利，如所有权、使用权、抵押权、典当权、租赁权等。</p><p><br/></p>', 19, 3, 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (15, 2, '经贸 投资', '<p><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);"><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp;随着中国经济的飞速发展和中国科技教育实力的增强，进入中国大学各专业院系学习的专业留学生日益增多，甚至超过了语言生。其中学习经济贸易专业的学生又占了很大比重。另外，中国各大学设立的专门针对外国留学生的汉语言本科学历教育，大部分也设定了汉语言经贸方向的培养目标，以适应飞速发展的中外经贸交流的需要。</span>&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp;投资指的是用某种有价值的</span><a target="_blank" href="http://baike.baidu.com/view/42564.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">资产</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">，其中包括</span><a target="_blank" href="http://baike.baidu.com/view/92610.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">资金</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">、</span><a target="_blank" href="http://baike.baidu.com/view/1280362.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">人力</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">、</span><a target="_blank" href="http://baike.baidu.com/view/18255.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">知识产权</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">等投入到某个企业、项目或经济活动，以获取经济回报的商业行为或过程。可分为</span><a target="_blank" href="http://baike.baidu.com/view/1987535.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">实物投资</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">、资本投资和</span><a target="_blank" href="http://baike.baidu.com/view/258256.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">证券投资</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">。前者是以</span><a target="_blank" href="http://baike.baidu.com/subview/26698/11206723.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">货币</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">投入企业，通过</span><a target="_blank" href="http://baike.baidu.com/view/3969629.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">生产经营活动</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">取得一定利润。后者是以货币购买企业发行的</span><a target="_blank" href="http://baike.baidu.com/view/2085.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">股票</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">和</span><a target="_blank" href="http://baike.baidu.com/view/156970.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">公司债券</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">，间接参与企业的</span><a target="_blank" href="http://baike.baidu.com/view/898782.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">利润分配</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">。证券投资的分析方法主要有如下三种：基本分析、技术分析、演化分析，其中基本分析主要应用于投资标的物的选择上，技术分析和演化分析则主要应用于具体投资操作的时间和空间判断上，作为提高投资分析有效性和可靠性的有益补充。</span></p>', 19, 3, 5, NULL, NULL, NULL, NULL, NULL, '2014-01-29 10:31:09', 1, NULL);
