INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (23, 17, 'XXX饭店', '<h3 style="margin: 20px 0px 10px; padding: 0px; font-size: 10.5pt;">职位描述</h3><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">岗位职责：</p><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">app服务器的功能实现，建设和维护。</p><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">承担核心的代码编写，负责系统改造和升级。</p><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">任职资格：</p><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">1. 扎实的JAVA基础；精通Java SE和Java EE技术。精通面向对象的分析和设计技术，包括设计模式、UML建模等；</p><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">2. 熟悉主流的开源框架，对Spring、Ibatis等有深入的了解，研究过源代码者优先。</p><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">3、熟悉使用mysql数据库</p><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">4、两年以上基于数据库的开发经验，熟练掌握SQL语句，能够熟练进行数据库设计；</p><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">5、有大型系统实践经验，参与过架构设计者优先</p><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">6、参与git相关的开源项目者,或了解amazon/google等大规模计算系统和关键技术优先;</p><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">7、有较强的独立工作能力和团队协作精神；</p><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">8、有较好的沟通表达能力；</p><ul class="more_detail list-paddingleft-2" style="list-style-type: none;"><li><p><strong style="margin: 0px; padding: 0px;">职位类别：&nbsp;</strong>Java程序员</p></li><li><p><strong style="margin: 0px; padding: 0px;">详细地址：&nbsp;</strong>深圳福田保税区深福保科技工业园B座301N</p></li><li><p><strong style="margin: 0px; padding: 0px;">学历要求：&nbsp;</strong>专科</p></li><li><p><strong style="margin: 0px; padding: 0px;">工作经验：&nbsp;</strong>1~3年</p></li><li><p><strong style="margin: 0px; padding: 0px;">工作性质：&nbsp;</strong>全职</p></li></ul><p><br/></p>', 19, 3, 2, NULL, NULL, NULL, NULL, NULL, '2014-01-29 10:33:45', 1, NULL);
INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (13, 9, '企业文化', '<p style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 12px; line-height: 22.799999237060547px; white-space: normal; background-color: rgb(255, 255, 255);"><strong>以德载天下</strong>&nbsp;<strong>以诚润乾坤</strong></p><p style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 12px; line-height: 22.799999237060547px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp; 作为一家综合性企业集团始终秉持德能载物，诚可立邦的崇高信念。昨天，<span style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 12px; line-height: 22.799999237060547px; background-color: rgb(255, 255, 255);">XX</span>从雨雪风霜中走来；今天，<span style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 12px; line-height: 22.799999237060547px; background-color: rgb(255, 255, 255);">XX</span>正在脚踏实地的奋进中；相信，明天的XX必将是物华秋实，硕果累累。</p><p>◇&nbsp;&nbsp; 企业宗旨：以德立业，以信润志</p><p>◇&nbsp;&nbsp; XX精神：团结、拼搏、突破、执着</p><p>◇&nbsp;&nbsp; 企业愿景：推崇务实的创业精神，不断提高技术与服务水平，勇敢承担社会责任，做一个受社会信赖与尊敬的企业。</p><p>◇&nbsp;&nbsp; 员工操守：先做人，后做事；欲正人，先正己。</p><p><br/></p>', 19, 3, 4, NULL, NULL, NULL, NULL, NULL, '2014-01-29 10:29:35', 1, NULL);
INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (12, 9, '经营战略', '<p>&nbsp; &nbsp;&nbsp;&nbsp;<strong>核心竞争力战略：</strong>加大核心产业投资力度，整合各相关产业资源，形成主业突出、市场竞争力强的产业体系。</p><p>&nbsp;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;<strong>&nbsp;综合经营战略：</strong>在强化核心业务的同时，寻找市场机遇，不断拓展新的经营领域，整合企业经营资源，丰富经营和服务手段，创造更多的利润增长点。</p><p>&nbsp;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>人力资源开发战略：</strong>人才是最宝贵的资源，既要为企业现有人才创造施展才华的舞台，同时充分利用人才市场，广泛吸纳社会各界精英，建立优势互补的“两条人才渠道”，不断优化人才结构。</p><p>&nbsp;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>名牌战略：</strong>积极培育和发展名牌商品，依靠强大的品牌提升核心竞争力。</p><p><br/></p>', 19, 3, 3, NULL, NULL, NULL, NULL, NULL, '2014-01-29 10:29:24', 1, NULL);
