INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (8, 1, '首页', '<p><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">大地震：通常用</span><a target="_blank" href="http://baike.baidu.com/view/66139.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">震级</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">来描述地震的规模。按照震级可以大致将地震的规模分为以下几类：大地震（震级大于等于7，</span><a target="_blank" href="http://baike.baidu.com/view/277655.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">砖木结构</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">房屋倒塌，钢筋水泥屋有少许开裂），中地震（震级大于等于5级小于7，较强震感，半跑半爬基本能移动），</span><a target="_blank" href="http://baike.baidu.com/view/1515335.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">小地震</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">（震级大于等于3小于5，有轻微震感，能行走，但有少许不稳，想喝醉酒一样），微小地震（震级大于等于1小于3，震感极小，吊灯会晃动），极微小地震（震级小于1，不能察觉，只能通过</span><a target="_blank" href="http://baike.baidu.com/view/65927.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">地震仪</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">记录）。在上述分类的基础上又特别将震级为8级和8级以上的大地震列为巨大地震，以强调其规模和破坏的巨大。</span></p>', 19, 2, 1, NULL, NULL, NULL, NULL, NULL, '2014-01-29 09:47:23', 1, 'home_07.gif');
