INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (10, 9, '企业介绍', '<p><br/></p><p><span style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 12px; line-height: 22.799999237060547px; background-color: rgb(255, 255, 255);">&nbsp; &nbsp; XX企业集团是一家多元化、跨地域的综合性企业集团，现有下属子公司近二十家，经营涉及房地产开发、黄金珠宝、IT产业、生物科技、物业管理、酒店餐饮、运动休闲、酒业、矿业、能源等多个领域，业务范围遍及全国。</span><br style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 12px; line-height: 22.799999237060547px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 12px; line-height: 22.799999237060547px; background-color: rgb(255, 255, 255);">&nbsp;</span><br style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 12px; line-height: 22.799999237060547px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 12px; line-height: 22.799999237060547px; background-color: rgb(255, 255, 255);">&nbsp; &nbsp; XX企业集团始终遵循“以德立业，以信润志”的企业宗旨，秉持“团结、拼搏、突破、执着”的企业精神，凭借优秀的管理思想、独到的经营理念、卓越的产品质量、雄厚的技术实力、细致高效的服务、务实的创业精神、诚实守信的商业信誉，博得了广大客户及众多合作伙伴的信赖与支持。</span><br style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 12px; line-height: 22.799999237060547px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 12px; line-height: 22.799999237060547px; background-color: rgb(255, 255, 255);">&nbsp;</span><br style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 12px; line-height: 22.799999237060547px; white-space: normal; background-color: rgb(255, 255, 255);"/><span style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 12px; line-height: 22.799999237060547px; background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp; 面对日益激烈的市场竞争，<span style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 12px; line-height: 22.799999237060547px; background-color: rgb(255, 255, 255);">XX</span>企业集团将一如既往地坚持自己的品牌理念与企业品格，不断从实践中总结经验，勇于进取，与社会各界通力合作，共谋企业发展并更好的回馈社会。</span></p><p><br/></p>', 19, 3, 1, NULL, NULL, NULL, NULL, NULL, '2014-01-29 10:29:04', 1, NULL);
INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (11, 9, '总裁致辞', '<p><strong>岁月如诗，风雨如歌。</strong></p><p>&nbsp;&nbsp;&nbsp;&nbsp;回望XX企业集团走过的道路：是XX人不断自强的道路，是XX人不断创新的道路。正是自强与创新的精神和意志，让XX企业集团走上了更为宽广、更为辉煌的经济舞台。XX由一家名不见经传的小企业起步，逐步转型为以房地产、高科技为基础的综合性企业集团，并不断提升核心竞争力，向着<span style="color: rgb(59, 51, 12); font-family: 宋体; font-size: 18px; line-height: 22.799999237060547px; background-color: rgb(255, 255, 255);">XX</span>人矢志不渝的梦想迈进，成为具有市场竞争力和行业影响力的集团公司。</p><p>&nbsp;</p><p><strong>雄关漫漫，长缨在手。</strong></p><p>&nbsp;&nbsp;&nbsp;&nbsp;昨日的辉煌如壮丽的风景，不断激励我们奋进的脚步。我们深知：市场竞争是一场没有终点的拉力赛。只有追求卓越，不断挑战自我的企业，才能始终保持领先。</p><p>&nbsp;</p><p>感谢您：曾经支持并见证了XX企业集团发展的朋友们，我们将共同创造更加雄丽的明天。</p><p><br/></p>', 19, 3, 2, NULL, NULL, NULL, NULL, NULL, '2014-01-29 10:29:15', 1, NULL);
INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (4, 2, '珠宝', '<p>&nbsp;&nbsp;&nbsp;&nbsp;一般习惯将金银等金属之外的天然材料（矿物、岩石、生物等）制成的，具有一定价值的首饰、工艺品或其他珍藏统称为珠宝，故有“金银珠宝”的说法，经营这些物品的珠宝行也统称为“珠宝行”。科学地说，“珠宝”与广义的“宝石”的概念是相同的。广义的宝石泛指那些适宜进行琢磨或雕刻加工为首饰或工艺品的原料。</p><p>宝石，必须具备以下几个特点：</p><p>珠宝大致包括：天然珠宝玉石（天然宝石、天然玉石、天然有机宝石）</p><p>人工宝石（人造宝石、再造宝石、拼合宝石、合成宝石），</p><p>还有首饰用贵金属：金、银、铂、钯。</p><p><br/></p>', 19, 3, 2, '2014-01-27', NULL, NULL, '2014-01-27', '2015-01-22', NULL, NULL, NULL);
INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (6, 2, '酒店餐饮 运动休闲', '<p><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp;酒店（又称为宾馆、旅馆、旅店、旅社、商旅、客店、客栈，台湾作饭店，港澳、马来西亚、新加坡等作酒店）其基本定义是提供安全、舒适，令利用者得到短期的休息或睡眠的空间的商业机构。一般地说来就是给宾客提供歇宿和饮食的场所。具体地说饭店是以它的建筑物为凭证，通过出售客房、餐饮及综合服务设施向客人提供服务，从而获得经济收益的组织。酒店主要为游客提供住宿服务、亦生活的服务及设施（寝前服务）、餐饮、游戏、娱乐、购物、商务中心、宴会及会议等设施。</span></p><p><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);"><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp;餐饮业（catering）是通过即时加工制作、商业销售和服务性劳动于一体，向消费者专门提供各种酒水、食品，消费场所和设施的食品生产经营行业。欧美《标准行业分类法》的定义，餐饮业是餐饮服务机构。</span></span></p><p><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);"><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);"><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp;运动，一种涉及体力和技巧的由一套规则或习惯所约束的活动，通常具有</span><a target="_blank" href="http://baike.baidu.com/view/941854.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">竞争性</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">。运动是物质的固有性质和存在方式，是物质所固有的根本属性，没有不运动的物质，也没有离开物质的运动。运动具守恒性，即运动既不能被创造又不能被消灭，其具体形式则是多样的并且互相转化，在转化中运动总量不变。</span></span></span></p><p><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);"><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);"><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">休闲是指在非劳动及非工作时间内以各种“</span><a target="_blank" href="http://baike.baidu.com/view/358525.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">玩</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">”的方式求得身心的调节与放松(haobc)，达到生命保健、体能恢复、身心愉悦的目的的一种业余生活。科学文明的休闲方式，可以有效地促进能量的储蓄和释放，它包括对智能、体能的调节和生理、心理机能的锻炼。</span></span></span></span></p>', 19, 3, 4, '2014-01-27', NULL, NULL, '2014-01-27', '2015-01-29', NULL, NULL, NULL);
INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (20, 16, '社会焦点', '<p><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp;焦点效应spotlight effect ，也叫做社会焦点效应，是人们高估周围人对自己外表和行为关注度的一种表现。焦点效应意味着人类往往会把自己看作一切的中心，并且直觉地高估别人对我们的注意程度。焦点效应其实是每个人都会有的体验，这种心理状态让我们过度关注自我，过分在意聚会或者工作集会时周围人们对我们的关注程度。正是因为每个人的焦点效应，在销售上也常常成为业务员的公关手段。</span></p><p><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">比喻事情的关键所在，或争论的集中点，或人们的关注集中点。 例：</span><a target="_blank" href="http://baike.baidu.com/view/2010.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">胡适</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">&nbsp;《〈</span><a target="_blank" href="http://baike.baidu.com/view/4793054.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">国学季刊</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">〉发刊宣言》：“大家的眼光与心力注射的</span><strong style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">焦点</strong><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">，究竟只在</span><a target="_blank" href="http://baike.baidu.com/view/51931.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">儒家</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">的几部经书。”&nbsp;</span><a target="_blank" href="http://baike.baidu.com/view/15673.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">茅盾</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">&nbsp;《</span><a target="_blank" href="http://baike.baidu.com/view/3948.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">子夜</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">》九：“估量着他每一句话的斤两，同时就感到目前的交涉非常棘手。赵伯韬所坚持的一项，就是</span><a target="_blank" href="http://baike.baidu.com/view/462275.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">吴荪甫</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">不肯让步的焦点。”</span></span></p>', 19, 3, 1, NULL, NULL, NULL, NULL, NULL, '2014-01-29 10:32:58', 1, NULL);
INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (21, 16, '企业新闻', '<p>&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">新闻，是指</span><a target="_blank" href="http://baike.baidu.com/view/13639.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">报纸</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">、</span><a target="_blank" href="http://baike.baidu.com/view/88567.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">电台</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">、</span><a target="_blank" href="http://baike.baidu.com/view/19299.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">电视台</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">、</span><a target="_blank" href="http://baike.baidu.com/view/6825.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">互联网</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">等</span><a target="_blank" href="http://baike.baidu.com/view/7072.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">媒体</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">经常使用的记录与传播</span><a target="_blank" href="http://baike.baidu.com/view/1527.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">信息</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">的一种</span><a target="_blank" href="http://baike.baidu.com/view/178539.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">文体</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">。新闻概念有广义与狭义之分。就其广义而言，除了发表于</span><a target="_blank" href="http://baike.baidu.com/view/867592.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">报刊</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">、</span><a target="_blank" href="http://baike.baidu.com/view/35385.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">广播</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">、</span><a target="_blank" href="http://baike.baidu.com/view/8623.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">电视</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">上的评论与专文外的常用文本都属于新闻之列，包括</span><a target="_blank" href="http://baike.baidu.com/view/89742.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">消息</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">、</span><a target="_blank" href="http://baike.baidu.com/view/20429.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">通讯</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">、</span><a target="_blank" href="http://baike.baidu.com/view/15438.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">特写</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">、</span><a target="_blank" href="http://baike.baidu.com/view/6451.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">速写</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">（有的将速写纳入特写之列）等等；狭义的新闻则专指消息，消息是用概括的叙述方式，比较简明扼要的文字，迅速及时地报道国内外新近发生的、有价值的的</span><a target="_blank" href="http://baike.baidu.com/view/2857.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">事实</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">。1943年</span><a target="_blank" href="http://baike.baidu.com/view/162423.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">陆定一</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">提出的，“新闻的定义，就是新近发生事实的</span><a target="_blank" href="http://baike.baidu.com/view/408955.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">报道</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">。”</span><a target="_blank" href="http://baike.baidu.com/view/49528.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">范长江</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">也对新闻下了一个定义，“新闻就是广大群众欲知应知而</span><a target="_blank" href="http://baike.baidu.com/subview/657018/5094665.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">未知</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">的</span><a target="_blank" href="http://baike.baidu.com/view/505621.htm" style="text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; white-space: normal; background-color: rgb(255, 255, 255);">重要</a><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);">事实”。</span></p><p><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);"></span></p><p>1.按事实发生状态分：有突发性新闻、持续性新闻、周期性新闻。</p><p>2.按事实发生与报道的时间差距分：有事件性新闻与<a target="_blank" href="http://baike.baidu.com/view/283284.htm" style="text-decoration: none; color: rgb(19, 110, 194);">非事件性新闻</a>。</p><p>3.按新闻发生的地区与影响范围分：有<a target="_blank" href="http://baike.baidu.com/view/41732.htm" style="text-decoration: none; color: rgb(19, 110, 194);">国际</a>性新闻、国内性新闻、地方性新闻。</p><p>4.按新闻事实的材料组合分：有典型新闻、综合新闻、系列新闻。</p><p>5.按传播渠道与信息载体分：有文字新闻、<a target="_blank" href="http://baike.baidu.com/view/19159.htm" style="text-decoration: none; color: rgb(19, 110, 194);">图片</a>新闻、电声新闻、音像新闻。</p><p>6.按反映社会生活的内容分：有政治新闻、<a target="_blank" href="http://baike.baidu.com/view/20838.htm" style="text-decoration: none; color: rgb(19, 110, 194);">经济</a>新闻、法律新闻、军事新闻、科技新闻、文教新闻、体育新闻、社会新闻等。</p><p><span style="color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);"><br/></span><br/></p>', 19, 3, 2, NULL, NULL, NULL, NULL, NULL, '2014-01-29 10:33:07', 1, NULL);
INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (22, 17, '集团总部', '<h3 style="margin: 10px 0px 5px; padding: 0px; font-size: 10.5pt; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;"><span style="font-size: 10.5pt;">职位1要求</span><br/></h3><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">岗位职责：&nbsp;<br style="margin: 0px; padding: 0px;"/>1、负责公司核心前端产品的研发；<span style="margin: 0px; padding: 0px;">&nbsp;</span>&nbsp;<br style="margin: 0px; padding: 0px;"/>2、调试前后端的接口制定和调试<br style="margin: 0px; padding: 0px;"/>3、实施前端项目的客户化定制；&nbsp;<br style="margin: 0px; padding: 0px;"/><br style="margin: 0px; padding: 0px;"/></p><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">任职资格：<span style="margin: 0px; padding: 0px;"><br style="margin: 0px; padding: 0px;"/>1.&nbsp;</span>说多了都是废话， 只要把下面一道题做出来就行（<span style="margin: 0px; padding: 0px;">js</span>实现）：<span style="margin: 0px; padding: 0px;"></span></p><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">1. 输入一个字符， 输出这个字符串中有三个（或以上）连续字母的子串。如输入“<span style="margin: 0px; padding: 0px;">abcfghdfddopqrst</span>”<span style="margin: 0px; padding: 0px;">,&nbsp;</span>输出<span style="margin: 0px; padding: 0px;">[&quot;abc&quot;, &quot;fgh&quot;,&quot;opqrst&quot;]</span>。<span style="margin: 0px; padding: 0px;"></span></p><p style="margin-top: 0px; margin-bottom: 10px; padding: 0px;">2. 会<span style="margin: 0px; padding: 0px;">actionscript</span>的加分。<strong style="margin: 0px; padding: 0px;"><span style="margin: 0px; padding: 0px;"></span></strong></p><ul class="more_detail list-paddingleft-2" style="list-style-type: none;"><li><p><strong style="margin: 0px; padding: 0px;">职位类别：&nbsp;</strong>前端开发工程师</p></li><li><p><strong style="margin: 0px; padding: 0px;">详细地址：&nbsp;</strong>北京市朝阳区XXXXXXXXXXXXXX</p></li><li><p><strong style="margin: 0px; padding: 0px;">学历要求：&nbsp;<