INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (29, 18, '留言版', '<p><strong><a target="_blank" href="http://baike.baidu.com/view/486953.htm" style="text-decoration: none; color: rgb(19, 110, 194);">电子公告牌系统</a></strong>（<strong>英语：Bulletin Board System</strong>，缩写<strong>BBS</strong>）是一种<a target="_blank" href="http://baike.baidu.com/view/37.htm" style="text-decoration: none; color: rgb(19, 110, 194);">软件</a>，允许用户使用终端程序通过调制解调器拨接或者因特网来进行连接，拥有下载数据或程序、上传数据、阅读新闻、与其它用户交换消息等功能。许多BBS由站长（通常被称为SYSOP，SYStem OPerator，原意为系统操作员）业余维护，而另一些则提供收费服务。</p><p>早期BBS的型态大多在终端机上以单色或彩色的纯文字呈现画面，并使用调制解调器拨接或telnet协议连接。但随着HTTP网络协议及HTML等网页技术的发展，BBS的型态也转变为<a target="_blank" href="http://baike.baidu.com/view/983754.htm" style="text-decoration: none; color: rgb(19, 110, 194);">网络论坛</a>或网络社群等多元样貌，早期拨接与telnet式的BBS系统则快速式微。然而在<a target="_blank" href="http://baike.baidu.com/view/2607.htm" style="text-decoration: none; color: rgb(19, 110, 194);">香港</a>、<a target="_blank" href="http://baike.baidu.com/view/2200.htm" style="text-decoration: none; color: rgb(19, 110, 194);">台湾</a>以及<a target="_blank" href="http://baike.baidu.com/view/327945.htm" style="text-decoration: none; color: rgb(19, 110, 194);">中国大陆</a>等地的学校中，telnet式的BBS系统仍然非常盛行，Template:Face。在<a target="_blank" href="http://baike.baidu.com/view/2607.htm" style="text-decoration: none; color: rgb(19, 110, 194);">香港</a>、<a target="_blank" href="http://baike.baidu.com/view/2200.htm" style="text-decoration: none; color: rgb(19, 110, 194);">台湾</a>以及<a target="_blank" href="http://baike.baidu.com/view/327945.htm" style="text-decoration: none; color: rgb(19, 110, 194);">中国大陆</a>等地的“BBS”一词通常是专指telnet式的BBS系统。</p><p><br/></p>', 19, 3, 1, NULL, NULL, NULL, NULL, NULL, '2014-01-29 10:35:01', 1, NULL);
INSERT INTO tb_column (id, parent_id, title, content, type, level, sort, publish_time, publish_user, update_time, start_time, end_time, create_time, create_id, image_url) VALUES (30, 18, '总裁信箱', '<p><strong>电子邮件</strong>（英语：<strong>e</strong>lectronic&nbsp;<strong>mail</strong>、简称：<strong>e-mail</strong>），又称<strong>电子函件</strong>、<strong>电邮</strong>或<strong>邮件</strong>，是指通过互联网进行书写、发送和接收信件，目的是达成发信人和收信人之间的信息交互。</p><p class="album-div" style="margin-top: 0px; margin-bottom: 0px; padding: 0px; position: relative; height: 117px; width: 220px;"><a hidefocus="true" class="album-overlay nslog:1212" href="http://baike.baidu.com/albums/47477/5413136/1/4051161.html#4051161$" target="_blank" style="text-decoration: none; color: rgb(19, 110, 194); display: block; position: absolute; left: 0px; height: 117px; width: 220px;"><img class="album-cover " alt="邮箱" title="邮箱" height="117" width="220" src="http://localhost/test/ueditor/upload/1392191377890.jpg" style="border: 0px; left: 0px; top: 0px; position: absolute;"/></a><span class="imgBorder" style="border: 1px solid rgb(224, 224, 224); position: absolute; top: 0px; left: 0px; display: block; height: 115px; width: 218px;"></span></p><p><a class="album-bottom" href="http://baike.baidu.com/albums/47477/5413136/1/4051161.html#4051161$" target="blank" style="text-decoration: none; color: rgb(19, 110, 194); display: block; min-height: 15px; line-height: 15px; padding: 8px 0px 8px 7px; text-align: left; border-width: 0px 1px 1px; border-right-style: solid; border-left-style: solid; border-right-color: rgb(224, 224, 224); border-left-color: rgb(224, 224, 224); border-bottom-style: solid; border-bottom-color: rgb(204, 204, 204);"><span class="title" style="font-size: 12px; line-height: 16px; margin: 0px; color: rgb(85, 85, 85); text-align: center;">邮箱</span><span class="albumCount" style="color: rgb(153, 153, 153); font-size: 12px; font-family: 宋体;">(10张)</span></a></p><p><a target="_blank" href="http://baike.baidu.com/view/8622.htm" style="text-decoration: none; color: rgb(19, 110, 194);">电子邮箱</a>（E-MAIL BOX）是通过网络<a target="_blank" href="http://baike.baidu.com/view/1068191.htm" style="text-decoration: none; color: rgb(19, 110, 194);">电子邮局</a>为网络客户提供的网络交流的电子<a target="_blank" href="http://baike.baidu.com/view/303341.htm" style="text-decoration: none; color: rgb(19, 110, 194);">信息空间</a>。电子邮箱具有<a target="_blank" href="http://baike.baidu.com/view/87682.htm" style="text-decoration: none; color: rgb(19, 110, 194);">存储</a>和<a target="_blank" href="http://baike.baidu.com/view/2752871.htm" style="text-decoration: none; color: rgb(19, 110, 194);">收发</a>电子信息的功能，是<a target="_blank" href="http://baike.baidu.com/view/1706.htm" style="text-decoration: none; color: rgb(19, 110, 194);">因特网</a>中最重要的信息交流工具。在网络中，电子邮箱可以自动接收网络任何电子邮箱所发的<a target="_blank" href="http://baike.baidu.com/view/1524.htm" style="text-decoration: none; color: rgb(19, 110, 194);">电子邮件</a>，并能存储规定大小的等多种格式的电子<a target="_blank" href="http://baike.baidu.com/view/345685.htm" style="text-decoration: none; color: rgb(19, 110, 194);">文件</a>。<a target="_blank" href="http://baike.baidu.com/view/3476.htm" style="text-decoration: none; color: rgb(19, 110, 194);">电子</a>邮箱具有单独的<a target="_blank" href="http://baike.baidu.com/view/85073.htm" style="text-decoration: none; color: rgb(19, 110, 194);">网络域名</a>，其电子邮箱的地址在@后标注。</p><p><br/></p>', 19, 3, 2, NULL, NULL, NULL, NULL, NULL, '2014-01-29 10:35:08', 1, NULL);
